mapper.select:
SELECT p.smallSortNum, ss.sortName smallSortName, TO_CHAR(requestDate,'YYYYMM') requestDate, SUM(NVL(productSum,0)) productSum FROM request r JOIN requestDetail rd ON r.requestNum = rd.requestNum JOIN product p ON rd.productNum = p.productNum JOIN smallSort ss ON p.smallSortNum = ss.smallSortNum WHERE p.smallSortNum = #{smallSortNum}
AND TO_CHAR(requestDate, 'YYYY') >= #{year} AND TO_CHAR(requestDate, 'YYYY') < #{year}+1
GROUP BY p.smallSortNum, ss.sortName, TO_CHAR(requestDate,'YYYYMM') ORDER BY requestDate ASC

SELECT rd.productNum, name, SUM(productCount) productCount, SUM(productSum) productSum FROM request r JOIN requestDetail rd ON r.requestNum = rd.requestNum LEFT OUTER JOIN product p ON rd.productNum = p.productNum
WHERE TO_CHAR(requestDate, 'YYYYMMDD') >= #{startDate} AND TO_CHAR(requestDate, 'YYYYMMDD') <=#{endDate} AND rd.productNum=#{productNum}
GROUP BY rd.productNum, name
