mapper.select:
SELECT q.qnaNum qnaNum, md.userId, nickName, q.num, q.subject qnaSubject, q.parent, NVL2(q2.parent, 1, 0) qnaIsAnswer, TO_CHAR(q.created, 'YYYY-MM-DD') qnaCreated, q.qnaCategoryNum, qnaCategory FROM qna q LEFT OUTER JOIN qna q2 ON q.qnaNum = q2.parent JOIN qnaCategory c ON q.qnaCategoryNum=c.qnaCategoryNum JOIN memberDetail md ON q.num=md.num
AND q.parent IS NULL AND q.num=#{num}
ORDER BY qnaNum DESC OFFSET #{offset} ROWS FETCH FIRST #{rows} ROWS ONLY
